        return new Source("{% extends 'base.html.twig' %}

{% block title %}Upload File{% endblock %}

{% block body %}
<div class=\"container mt-5\">
    <h2>Upload a File</h2>

    <form action=\"{{ path('file_upload') }}\" method=\"post\" enctype=\"multipart/form-data\" class=\"mt-4\">

        <div class=\"form-group\">
            <label for=\"file\"><strong>Choose a file:</strong></label>
            <div class=\"custom-file\">
                <input type=\"file\" class=\"custom-file-input\" id=\"file\" name=\"file\" required>
                <label class=\"custom-file-label\" for=\"file\">Choose file...</label>
            </div>
        </div>

        <div class=\"form-group mt-4\">
            <label for=\"description\"><strong>Description (optional):</strong></label>
            <textarea name=\"description\" id=\"description\" rows=\"3\" class=\"form-control\" placeholder=\"Add a description for your file...\"></textarea>
        </div>

        <button type=\"submit\" class=\"btn btn-primary mt-2\">Upload</button>
    </form>
</div>

{% endblock %}

{% block stylesheets %}
    <link rel=\"stylesheet\" href=\"https://stackpath.bootstrapcdn.com/bootstrap/4.5.2/css/bootstrap.min.css\">
    <link rel=\"stylesheet\" href=\"https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.15.1/css/all.min.css\">
{% endblock %}

{% block javascripts %}
<script src=\"https://code.jquery.com/jquery-3.5.1.min.js\"></script>
<script>
\$(document).ready(function() {
    \$('.custom-file-input').on('change', function() {
        let fileName = \$(this).val().split('\\\\').pop();
        \$(this).next('.custom-file-label').addClass(\"selected\").html(fileName);
    });
});
</script>
{% endblock %}
